import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { map } from 'rxjs';
import { WeatherService } from '../weather.service';
import { Forecast } from '../forecast.model';

@Component({
  selector: 'app-forecast',
  templateUrl: './forecast.component.html',
  styleUrls: ['./forecast.component.css']
})
export class ForecastComponent implements OnInit {
  zipcode: string = "";
  forecast: Forecast;

  constructor(
    private route: ActivatedRoute,
    private weatherService: WeatherService,
    private router: Router) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.zipcode = params['zipcode']
      console.info(this.zipcode);
      this.weatherService.getForecast(this.zipcode).subscribe(response => {
        this.forecast = response;
        this.forecast.list.forEach(weather => { weather.icon = this.weatherService.getIcon(weather.weather[0].description) })
      });
    });

  }

  redirectToHome() {
    this.router.navigate(["/"]);
  }

}
<div class="container-fluid" *ngIf="forecast">
  <div class="panel panel-default">
    <div class="panel-heading">
      <h3 class="panel-title">5-day forecasting for {{forecast.city.name}}</h3>

    </div>
    <ul class="list-group">
      <li class="list-group-item" *ngFor="let weather of forecast.list">
        {{weather.dt_txt}}:
        {{weather.weather[0].description}}
        - Min: {{weather.main.temp_max}}
        - Max: {{weather.main.temp_min}}

        <img class="icon" src="`{{weather.icon">
      </li>
    </ul>
  </div>
  <button class="btn btn-success" tabindex="0" id="mainPageBtn" (click)="redirectToHome()">&lt; Back to main page</button>
