import { Component } from '@angular/core';
import { Subscription } from 'rxjs';
import { ZipcodeService } from '../zipcode.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  zipcodeSubscription = new Subscription();

  zipcodes: string[] = [];
  constructor(private zipcodeService: ZipcodeService) { }

  ngOnInit(): void {
    //console.info('init')
    this.zipcodeSubscription = this.zipcodeService.zipcodes$.subscribe((z: string[]) => {
      console.info(z);
      this.zipcodes = z;
    });
  }
}
<app-zipcode-form></app-zipcode-form>
<app-weather-report *ngFor="let zipcode of zipcodes" [zipcode]="zipcode"></app-weather-report>
