import { Component, Input, OnInit } from '@angular/core';
import { WeatherService } from '../weather.service';
import { Weather } from '../weatherinfo.model';
import { ZipcodeService } from '../zipcode.service';

@Component({
  selector: 'app-weather-report',
  templateUrl: './weather-report.component.html',
  styleUrls: ['./weather-report.component.css']
})
export class WeatherReportComponent implements OnInit {

  @Input() zipcode: string = "";

  constructor(
    private weatherService: WeatherService,
    private zipcodeService: ZipcodeService
  ) { }

  weather: Weather;
  icon: string;

  ngOnInit()
  {
    console.info('init');
    this.weatherService.getWeatherReport(this.zipcode).subscribe(response =>
    {
      this.weather = response;
      this.weather.icon = this.weatherService.getIcon(this.weather.weather[0].description);
      //console.info(this.weather)
    });
  }

  deleteZipcode()
  {
    this.zipcodeService.deleteZipcode(this.zipcode);
  }

}
<div class="container-fluid" *ngIf="weather">
    <div>
      <div class="well flex">
        <div>
          <h3>{{zipcode}} {{weather.name}}</h3>
          <h4>Current conditions: {{weather.weather[0].description ?? "unknown"}}</h4>
          <h4>Temperatures today:</h4>
          <p>
            Current: {{weather.main.temp}}
            - Max {{weather.main.temp_max}}
            - Min {{weather.main.temp_min}}
          </p>
          <p>
            <a href="/forecast/{{zipcode}}">Show 5-day forecast for {{weather.name}}</a>
          </p>
        </div>
        <div>
          <button class="close" (click)="deleteZipcode()">Close</button>
          <img src="{{icon}}">
        </div>
      </div>
    </div>
  </div>
