import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { map } from 'rxjs';
import { WeatherService } from '../weather.service';
import { Weather } from '../weatherinfo.model';
import { Forecast } from '../forecast.model';

@Component({
  selector: 'app-forecast',
  templateUrl: './forecast.component.html',
  styleUrls: ['./forecast.component.css']
})
export class ForecastComponent implements OnInit {
  private zipcode: string = "";
  forecast: Forecast;

  constructor(private route: ActivatedRoute, private weatherService: WeatherService) { }

  ngOnInit(): void {
    this.route.params.pipe(map(params => { this.zipcode = params['zipcode'] }));
    this.weatherService.getForecast(this.zipcode).subscribe(response => {
      this.forecast = response;
    this.forecast.list.forEach(weather => {weather.icon = this.weatherService.getIcon(weather.weather[0].description)})});
  }

}
<div class="container-fluid">
  <div class="panel panel-default">
    <div class="panel-heading">
      <h3 class="panel-title">5-day forecast for {{forecast.city.name}}</h3>

    </div>
    <ul class="list-group">
      <li class="list-group-item" *ngFor="let weather in forecast.list">
        {{weather.dt_txt}}:
        {{weather.weather.description}}
        - Min: {{weather.main.temp_max}}
        - Max: {{weather.main.temp_min}}

        <img class="icon" src="{{weather.icon}}">
      </li>
    </ul>
  </div>
  <button class="btn btn-success" tabindex="0" id="mainPageBtn">&lt; Back to main page</button>
